const initialState = {
  todos: [],
  visibilityFilter: VisibilityFilters.SHOW_ALL,
};


const todos = (initialState, action) => {
    switch (action.type) {
      case 'ADD_TODO':
        return [
          ...state,
          {
            id: action.id,
            text: action.text,
            completed: false,
          },
        ];
      case 'TOGGLE_TODO':
        return state.map((todo) =>
          todo.id === action.id
            ? { ...todo, completed: !todo.completed }
            : todo
        ); 
      case 'SEARCH_TODO':
      return {
        ...state,
        todos: state.todos.filter(todo => {
          if (action.filter === VisibilityFilters.SHOW_FINDED) {
            return todo.text.includes(action.text) && todo.completed;
          } else {
            return true;
          }
        }),
      };           
      default:
        return state;
    }
  };
  
  export default todos;
