const todos = (state = [], action) => {
    switch (action.type) {
      case 'ADD_TODO':
        return [
          ...state,
          {
            id: action.id,
            text: action.text,
            completed: false,
          },
        ];
      case 'TOGGLE_TODO':
        return state.map((todo) =>
          todo.id === action.id
            ? { ...todo, completed: !todo.completed }
            : todo
        );
      case 'SEARCH_TODO':
        return state.map((todo) =>
          todo.text.includes(action.text))
            ? [ ...state, todo ] : state
      default:
        return state;
    }
  };
  
  export default todos;
