import React from 'react';
import { connect } from 'react-redux';
import { searchTodo, VisibilityFilters } from '../actions';
import './SearchTodo.css';

const SearchTodo = ({ dispatch }) => {
  let input;

  return (
    <div className='textTodo'>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          if (!input.value.trim()) {
            return;
          }
          dispatch(searchTodo(input.value, VisibilityFilters.SHOW_FINDED));
          input.value = '';
        }}
      >
        <input className='bar' ref={(node) => (input = node)} />
        <button className='add' type="submit">Search</button>
      </form>
    </div>
  );
};

export default connect()(SearchTodo);
